import { View, Image, Text, Button, StyleSheet, Pressable } from "react-native";

const Item = (props) => {
    return (
        <Pressable 
            style={styles.item(props.item.choosen)} 
            onPress={()=>{
                console.log('pressed')
                //console.log(props.item.choosen)
                props.item.choosen = !props.item.choosen
            }}
        >
            <Image style={styles.imageItem} source={props.item.imageSource}/>
            <View style={styles.itemText}>
                <Text style={{fontWeight: 'bold'}}>{props.item.name}</Text>
                <Text style={{flexWrap: 'wrap'}}>{props.item.description}</Text>
                <Text style={{color: 'orange', fontSize: 20}}>{props.item.price}</Text>
            </View>

            <View style={styles.button}>
                <Button title='-' color={'orange'} onPress={()=>{props.item.amount>0 ? setItem({...props.item, amount: props.item.amount--}) : ''; console.log('b1',props.item.amount)}}/>
                <Text>{props.item.amount}</Text>
                <Button title='+' color={'orange'} onPress={()=>{setItem({...props.item, amount: props.item.amount++}); console.log('b2',props.item.amount)}}/>
                {console.log('here2',props.item.amount)}
            </View>
            {console.log('here',props.item.amount)}
        </Pressable>
    );
}

const styles = StyleSheet.create({
    imageItem:{
      width: 50,
      height: 100
    },
    item: ifChoosen => ({
      flexDirection: 'row',
      width: '85%',
      height: '15%',
      justifyContent: 'flex-start',
      alignItems: 'center',
      marginTop: 20,
      shadowColor: ifChoosen ? "green" : "#000000",
      shadowOpacity: 0.8,
      shadowRadius: 2,
      shadowOffset: {
        height: 1,
        width: 1
      }
    }),
    itemText:{
      flexDirection: 'column',
      alignContent: 'flex-start',
      marginLeft: 20,
      width: '60%'
    },
    button: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: 'orange',
      width: 40,
      height: 35
    }
});

export default Item;
