import { Text, View, StyleSheet } from "@shopify/react-native-skia"
import OrderOption from './OrderOption';

const PlacingOrder = (props) => {
    return (
        <>
            <Text style={{fontWeight: '500', marginTop: 20}}>Оформление заказа</Text>
            <View style={styles.orderInfo}>
              {
                orderOptions.map((option, idx)=>{
                  return <OrderOption option={option} key={idx}/>
                })
              }
              <View style={{flexDirection: 'row', marginTop: 20}}>
                <View style={{flexDirection: 'column'}}>
                  <Text>{processPath.productsAmount} товара</Text>
                  <Text>Доставка</Text>
                  <Text>Начислим баллы</Text>
                </View>
                <View style={{flexDirection: 'column', marginLeft: 60}}>
                  <Text style={{color: 'orange'}}>{props.totalPrice} p.</Text>
                  <Text style={{color: 'orange'}}>Бесплатно</Text>
                  <Text style={{color: 'orange'}}>0</Text>
                </View>
              </View>
            </View>
          </>
    )
}

const styles = StyleSheet.create({
    orderInfo: {
      alignContent: 'flex-start',
      width: '90%',
      marginTop: 5,
      paddingTop: 5,
      paddingRight: 20,
      paddingLeft: 20,
      paddingBottom: 20,
      shadowColor: "#000000",
      shadowOpacity: 0.8,
      shadowRadius: 2,
      shadowOffset: {
        height: 1,
        width: 1
      }
    }
})

export default PlacingOrder;
