import { View, Image, Text, Button, StyleSheet, Pressable } from "react-native";

const Item = (props) => {
  function test(propertyName, value){
    const newArray = [...props.items] 
    newArray[props.idx] = {...props.item, [propertyName]: value}
    props.setItems(newArray)
  }
  return (
      <Pressable 
        style={styles.item(props.item.choosen)} 
        onPress={()=>{
          test('choosen', !props.item.choosen)
        }}
      >
        <Image style={styles.imageItem} source={props.item.imageSource}/>
        <View style={styles.itemText}>
          <Text style={{fontWeight: 'bold'}}>{props.item.name}</Text>
          <Text style={{flexWrap: 'wrap'}}>{props.item.description}</Text>
          <Text style={{color: 'orange', fontSize: 20}}>{props.item.price}</Text>
        </View>

        <View style={styles.button}>
          <Button title='-' color={'orange'} onPress={()=>{props.item.amount>0 ? test('amount', props.item.amount--) : ''}}/>
          <Text>{props.item.amount}</Text>
          <Button title='+' color={'orange'} onPress={()=>{test('amount', props.item.amount++)}}/>
        </View>
      </Pressable>
  );
}

const styles = StyleSheet.create({
    imageItem:{
      width: 50,
      height: 100
    },
    item: ifChoosen => ({
      flexDirection: 'row',
      width: '85%',
      height: '15%',
      justifyContent: 'flex-start',
      alignItems: 'center',
      marginTop: 20,
      borderColor: ifChoosen ? "green" : "", 
      borderWidth: ifChoosen ? 5 : 0,
      shadowColor: "#000000",
      shadowOpacity: 0.8,
      shadowRadius: 2,
      shadowOffset: {
        height: 1,
        width: 1
      }
    }),
    itemText:{
      flexDirection: 'column',
      alignContent: 'flex-start',
      marginLeft: 20,
      width: '60%'
    },
    button: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: 'orange',
      width: 40,
      height: 35
    }
});

export default Item;
