import { View, Image, Text, StyleSheet } from 'react-native';

const Header = () => {
    return (
        <View style={styles.header}>
            <Image style={styles.image} source={require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/delivery.png')}/>
            <View style={styles.headerText}>
                <Text style={{color: 'orange'}}>Доставка курьером</Text>
                <Text>ул. Миклухо-Маклая, 55, Москва</Text>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    header: {
        flexDirection: 'row',
        width: '85%',
        alignContent: 'flex-start',
        justifyContent: 'flex-start',
        marginTop: 10
      },
      headerText:{
        flexDirection: 'column', 
        alignItems:'center',
        width: '90%'
      },
      image:{
        width: 20,
        height: 20
      }
})

export default Header;
