import { View, Image, Text, Button, StyleSheet, Pressable } from "react-native";

const Item = (props) => {
    return (
        <View style={styles.item(props)}>
            <Image style={styles.imageItem} source={props.item.imageSource}/>
            <View style={styles.itemText}>
                <Text style={{fontWeight: 'bold'}}>{props.item.name}</Text>
                <Text style={{flexWrap: 'wrap'}}>props.item.description</Text>
                <Text style={{color: 'orange', fontSize: 20}}>props.item.price</Text>
            </View>

            <Pressable style={styles.button} onPress={props.item.choosen = !props.item.choosen}>
                <Button title='-' color={'orange'}/>
                <Text>{props.item.amount}</Text>
                <Button title='+' color={'orange'}/>
            </Pressable>
        </View>
    );
}

const styles = StyleSheet.create({
    imageItem:{
      width: 50,
      height: 100
    },
    item: props => ({
      flexDirection: 'row',
      width: '85%',
      height: '15%',
      justifyContent: 'flex-start',
      alignItems: 'center',
      marginTop: 20,
      shadowColor: props.item.choosen ? "green" : "#000000",
      shadowOpacity: 0.8,
      shadowRadius: 2,
      shadowOffset: {
        height: 1,
        width: 1
      }
    }),
    itemText:{
      flexDirection: 'column',
      alignContent: 'flex-start',
      marginLeft: 20,
      width: '60%'
    },
    button: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: 'orange',
      width: 40,
      height: 35
    }
});

export default Item;
