/*
Верстка по макету - https://www.figma.com/file/QlpfSixiQu7anmqurRuGQw/%D0%94%D0%B8%D0%B7%D0%B0%D0%B9%D0%BD-%D0%BC%D0%BE%D0%B1%D0%B8%D0%BB%D1%8C%D0%BD%D0%BE%D0%B3%D0%BE-%D0%BF%D1%80%D0%B8%D0%BB%D0%BE%D0%B6%D0%B5%D0%BD%D0%B8%D1%8F-%2F-Mobile-app-design-(Community)?node-id=1%3A2&mode=dev
      * При зажатии на продукте - он стает активным, его рамка стает зеленная
        Если был активный и мы зажали еще раз - активность пропадает
      * Сверстать пункт доставка курьером
      * Делать разбивку на компоненты
      * При нажатии на кнопку назад   - пишет в консоль назад
      * При нажатии на кнопку корзина - активный элемен удаляется
      * При нажатии на cтрелочки + -   - увеличивается количество продуктов
      * При нажатии на перейти к оплате - выводит продукты которые есть и их количество
      * ВЫПАДУЮЩИЕ СПИСКИ (Банковская карта, курьеом и т.д) пока не открываются
*/

import { useState } from 'react';
import { StyleSheet, View, Text, Image, Button} from 'react-native';
import Header from './components/Header';
import Item from './components/Item';

export default function App() {
  const [items, setItems] = useState([
    {
      imageSource: require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/item1.png'),
      name: 'Мойка RE 130 Plus',
      description: 'Мощная мойка высокого давления (135 бар) повышенной комфортности.',
      price: '49 990р.',
      amount: 0,
      choosen: false
    },
    {
      imageSource: require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/item2.png'),
      name: 'Мотокоса FS 38',
      description: 'Лёгкая мотокоса мощностью 0,65 кВт',
      price: '17 990р.',
      amount: 0,
      choosen: false
    }
  ])

  const [orderOptions, setOrderOption] = useState([
    {
      imageSource: require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/delivery.png'),
      title: 'Выберите тип доставки',
      buttonTitle: 'Курьером'
    },
    {
      imageSource: require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/clock.png'),
      title: 'Выберите время и дату доставки',
      buttonTitle: '12:00 05.09.2022'
    },
    {
      imageSource: require('C:/Users/renat/Desktop/Prog/Lessons/lesson16/shop/assets/payment.png'),
      title: 'Выберите способ оплаты',
      buttonTitle: 'Банковская карта'
    }
  ])

  function componentSetItems(idx, propertyName, value){
    const newArray = [...items] 
    newArray[idx] = {...items[idx], [propertyName]: value}
    setItems(newArray)
  }

  function titleWidth(title){
    const titleFont = useFont(sfMono, 64)
    return titleFont.getTextWidth(title)
  }

  return (
    <View style={styles.container}>
      <Header items={items} setItems={setItems}/>
      {
        items.map((item, idx)=>{
          return <Item item={item} setItems={componentSetItems} idx={idx} key={idx}/>
        })
      }

      <Text style={{fontWeight: '500', marginTop: 20}}>Оформление заказа</Text>
      <View style={styles.orderInfo}>
        {
          orderOptions.map((option)=>{
            return <>
              <View style={{flexDirection: 'row', marginTop: 10}}>
                <Image style={styles.image} source={option.imageSource}/>
                <Text>{option.title}</Text>
              </View>
              <View style={styles.button(titleWidth())}>
                <Button title={option.buttonTitle} color={'orange'}/>
              </View>
            </>
          })
        }
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center'
  },
  button: titleWidth => ({
    backgroundColor: 'orange',
    width: titleWidth,
    marginTop: 8
  }),
  image:{
    justifyContent: 'flex-end',
    width: 20,
    height: 20,
    marginRight: 8
  },
  orderInfo: {
    alignContent: 'flex-start',
    width: '85%',
    marginTop: 5,
    paddingTop: 5,
    paddingRight: 20,
    paddingLeft: 20,
    paddingBottom: 20,
    shadowColor: "#000000",
    shadowOpacity: 0.8,
    shadowRadius: 2,
    shadowOffset: {
      height: 1,
      width: 1
    }
  }
});
