import { View, Image, Text, Button, StyleSheet, Pressable } from "react-native";

const Item = (props) => {
  return (
      <Pressable 
        style={styles.item(props.item.choosen)} 
        onLongPress={()=>{
          props.updateProduct(props.idx, 'choosen', !props.item.choosen)
        }}
      >
        <Image style={styles.image} source={props.item.imageSource}/>
        <View style={styles.itemText}>
          <Text style={{fontWeight: 'bold'}}>{props.item.name}</Text>
          <Text style={{flexWrap: 'wrap'}}>{props.item.description}</Text>
          <Text style={{color: '#EE7100', fontSize: 20, fontWeight: '500'}}>{props.item.price} p.</Text>
        </View>

        {
          props.pressedBuy ?
            <Text style={{color: '#EE7100', fontSize: 20, marginLeft: 20}}>X{props.item.amount}</Text>         
          :
          <View style={styles.button}>
              <Button title='-' color={'#EE7100'} onPress={()=>{props.item.amount>0 ? props.updateProduct(props.idx, 'amount', props.item.amount-1) : ''}}/>
              <Text>{props.item.amount}</Text>
              <Button title='+' color={'#EE7100'} onPress={()=>{props.updateProduct(props.idx, 'amount', props.item.amount+1)}}/>
          </View>
        }
      </Pressable>
  );
}

const styles = StyleSheet.create({
    image:{
      width: 50,
      height: 100
    },
    item: ifChoosen => ({
      flexDirection: 'row',
      width: '90%',
      height: '15%',
      justifyContent: 'flex-start',
      alignItems: 'center',
      borderRadius: '20px',
      //marginTop: 20,
      borderColor: ifChoosen ? "green" : "", 
      borderWidth: ifChoosen ? 5 : 0,
      shadowColor: "#000000",
      shadowOpacity: 0.8,
      shadowRadius: 2,
      shadowOffset: {
        height: 1,
        width: 1
      }
    }),
    itemText:{
      flexDirection: 'column',
      alignContent: 'flex-start',
      marginLeft: 20,
      width: '60%'
    },
    button: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#EE7100',
      width: 40,
      height: 35
    }
});

export default Item;
