const OrderOption = (props) => {

    return (
        <>
            <View style={{flexDirection: 'row', marginTop: 10}}>
                <Image style={styles.image} source={props.option.imageSource}/>
                <Text>{props.option.title}</Text>
            </View>
                <View style={styles.button}>
                <Button title={props.option.buttonTitle} color={'orange'}/>
            </View>
        </>
    );
}

const styles = StyleSheet.create({
    button: {
      backgroundColor: 'orange',
      marginTop: 8,
    },
    image: {
      justifyContent: 'flex-end',
      width: 20,
      height: 20,
      marginRight: 8
    }
});

export default OrderOption;
